import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { _getPinfosList } from "../../util/req";
import { API_KEY } from "./../Home";

export default function Index() {
  const { place_id, wait } = useParams();

  const [info, setInfo] = useState({});

  useEffect(() => {
    const getInfo = async () => {
      var data = await _getPinfosList(
        `/details/json?&key=${API_KEY}&placeid=${place_id}`
      );
      console.log("data.result -> :", data.result);
      setInfo({ ...data.result, wait });
    };
    getInfo();
  }, [place_id, wait]);

  return (
    <div className="info-center">
      <p>Name:{info.name}</p>
      <ul>
        {info.photos.map((it, index) => (
          <li key={index}>
            <img src={it} alt="" />
          </li>
        ))}
      </ul>
    </div>
  );
}
