import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import { _getPlacesLists } from "./../../util/req";
export default function Index() {
  const [List, setList] = useState([]);
  const [sort, setSort] = useState(false);

  const getList = async () => {
    var data = await _getPlacesLists();
    var mpdata = data.data.map((it, index) => ({
      ...it,
    }));
    setList(mpdata);
    console.log("data -> :", mpdata);
  };
  useEffect(() => {
    getList();
  }, []);
  useEffect(() => {
    if (sort) {
      const nwList = List;
      nwList.sort((a, b) => a.hospName - b.hospName);
      setList([...nwList]);
    } else {
      const nwList = List;
      nwList.sort((a, b) => b.hospName - a.hospName);
      setList([...nwList]);
    }
  }, [List, sort]);

  return (
    <div className="info-center">
      <br />
      <br />
      <br />
      <table>
        <thead>
          <tr>
            <th>
              HOSPNAME{" "}
              {sort ? (
                <span
                  onClick={() => {
                    setSort(false);
                  }}
                >
                  '↑'
                </span>
              ) : (
                <span
                  onClick={() => {
                    setSort(true);
                  }}
                >
                  '↓'
                </span>
              )}
            </th>
            <th>NAME</th>
            <th>FORMATTED_ADDRESS</th>
            <th>WEBSITE</th>
            <th>TOPWAIT</th>
            <th>RATING</th>
            <th>ICON</th>
          </tr>
        </thead>
        <tbody>
          {List.map((it, index) => (
            <tr key={index}>
              <td>
                <Link to={`/index/info/${it.place_id}`}>
                  {it.wait.hospName}
                </Link>
              </td>
              <td>{it.name}</td>
              <td>{it.formatted_address}</td>
              <td>{it.website}</td>
              <td>{it.wait.topWait}</td>
              <td>{it.rating}</td>
              <td>{it.icon}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
