import React, { useEffect, useState } from "react";
import { _getUserList, _delUser, _reg, _update } from "../../util/req";
import Button from '../button'
import ModalWithTitle from '../ModalWithTitle'
import "./style.css"

const AddModal = (props) => {
	const gelist = async () => {
    var res = await _getUserList();
    setList(res.data);
	};
	const [list, setList] = useState([]);
	const save = async () => {

    if (infoForm._id) {
      await _update(infoForm);
    } else {
      await _reg(infoForm);
    }
    alert("ok");
    gelist();
		setInfoForm({ username: "", password: "" });

  };
	const [infoForm, setInfoForm] = useState({
    username: "",
    password: "",
	});
	const { update = false ,className} = props
	const {title} = update ? "Update User": "Add New User"
  return(
    <ModalWithTitle englishTitle={title} onClick={props.onClickClose} backgroundColor="#1F6332">
		<div className="addmodal-container">
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					User Name:{" "}
				</span>
			</div>
			<div className="addmodal-input-container">
				<input className="addmodal-input"
					type="text" 
					value={infoForm.username}
					onChange={(e) => {
						setInfoForm({ ...infoForm, username: e.target.value }); 
					}}
				/>
			</div>
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					Password:{" "}
				</span>
			</div>
      <div className="addmodal-input-container">
				<input className="addmodal-input" 
						type="text" 
            value={infoForm.password}
            onChange={(e) => {
              setInfoForm({ ...infoForm, password: e.target.value });
					}}
				/>
			</div>
			<div className="addmodal-button-container">
				<div className="addmodal-button">
					<Button chineseLabel="確認" englishLabel="Confirm" onClick={save(),props.onClickClose} >
						{infoForm._id ? "Save" : "Create"}
					</Button>
				</div>
      </div>
		</div>	
  	</ModalWithTitle>
  )

}

export default AddModal
