import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import './style.css'
import { _getPlacesLists } from "./../../util/req";
//import { useTable } from "react-table";

export default function Index() {
  const [List, setList] = useState([]);
  const [sort, setSort] = useState(false);

  /*
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    setFilter // The useFilter Hook provides a way to set the filter
  } = useTable(
    {
      columns,
      data
    },
    useFilters // Adding the useFilters Hook to the table
    // You can add as many Hooks as you want. Check the documentation for details. You can even add custom Hooks for react-table here
  );*/

  const getList = async () => {
    var data = await _getPlacesLists();
    var mpdata = data.data.map((it, index) => ({
      ...it,
    }));
    mpdata.sort(
      (a, b) => a.wait.hospName.charCodeAt(0) - b.wait.hospName.charCodeAt(0)
    );
    setList(mpdata);
    console.log("data -> :", mpdata);
  };

  // Create a state
const [filterInput, setFilterInput] = useState("");

// Update the state when input changes
const handleFilterChange = e => {
  const value = e.target.value || undefined;
  //setFilter("show.name", value); 
  setFilterInput(value);
};




  useEffect(() => {
    getList();
  }, []);
  useEffect(() => {
    const nwList = List;
    if (sort) {
      nwList.sort(
        (a, b) => a.wait.hospName.charCodeAt(0) - b.wait.hospName.charCodeAt(0)
      );
    } else {
      nwList.sort(
        (a, b) => b.wait.hospName.charCodeAt(0) - a.wait.hospName.charCodeAt(0)
      );
    }
    console.log("nwList -> :", nwList);

    setList(nwList);
  }, [List, sort]);
  
  const user = localStorage.getItem("user")
  ? JSON.parse(localStorage.getItem("user"))
  : {};
  console.log(user.station)
  if (user.station== undefined){
    this.props.history.push("/login")
  }

  return (
    <div style={{ overflowX: "auto" }} className="page-container">
      <br />
      <br />
    <input
      value={filterInput}
     onChange={handleFilterChange}
     placeholder={"Search hospital name, address, website, etc."}
    />
      <br />

      <table className="table">
        <thead>
          <tr>
            <td style={{ width: "24%" }}>
              HOSPNAME{" "}
              {sort ? (
                <span
                  onClick={() => {
                    setSort(false);
                  }}
                >
                  ↑
                </span>
              ) : (
                <span
                  onClick={() => {
                    setSort(true);
                  }}
                >
                  ↓
                </span>
              )}
            </td>
            <td>NAME</td>
            <td>FORMATTED_ADDRESS</td>
            <td>WEBSITE</td>
            <td>TOPWAIT</td>
            <td>RATING</td>
            <td>ICON</td>
          </tr>
        </thead>
        <tbody>
          {List.map((it, index) => (
            <tr key={index}>
              <td>
                <Link to={`/index/info/${it.place_id}`}>
                  {it.wait.hospName}
                </Link>
              </td>
              <td>{it.name}</td>
              <td>{it.formatted_address}</td>
              <td>
                <a href={it.website}>{it.website}</a>
              </td>
              <td>{it.wait.topWait}</td>
              <td>{it.rating}</td>
              <td>
                <img src={it.icon} width="50px" height="50px" alt="" />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
