import React, { useEffect, useState } from "react";
import { _getUserList, _delUser, _reg, _update } from "../../util/req";
import AddModal from '../../component/AddModal';
import Button from '../../component/button';
import './style.css';

export default function Index() {
  const [showAddModal,setAddModal] = useState(false);
  const [showUpdateModal,setUpdateModal] = useState(false);
  const handleClickAdd = () => {
    setAddModal(true)
  }
  const handleCloseModal = () => {
    setAddModal(false)
    gelist();
  }
  const [list, setList] = useState([]);
  const [infoForm, setInfoForm] = useState({
    username: "",
    password: "",
  });

  const gelist = async () => {
    var res = await _getUserList();
    setList(res.data);
  };
  const del = async (id) => {
    await _delUser(id);
    gelist();
    alert("User Deleted!");
  };
  
  const update = async (it) => {
    setInfoForm({ ...it });
    setUpdateModal();
  };

  useEffect(() => {
    gelist();
  }, []);

  return (
    <div className="page-container">
      <br />
      <br />
      <div className="button-container">
      <div className="user-button">
        <Button  onClick={handleClickAdd} englishLabel="Create User"/>
      </div>
      </div>
      
  


      <table className="table">
        <thead>
          <tr>
            <td>Username</td>
            <td>Password</td>
            <td>Action</td>
          </tr>
        </thead>
        <tbody>
          {list.map((it, index) => (
            <tr key={index}>
              <td>{it.username}</td>
              <td>{it.password}</td>
              <td>
                <div className="table-action-container">
                  <div className="user-button">
                    <Button className="up-del-button" englishLabel="Update" onClick={() => update(it)}/>
                  </div>
                  <div className="user-button">
                    <Button englishLabel="Delete" onClick={() => del(it._id)}/>
                  </div>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {showAddModal&&<AddModal className="admin-modal" onClickClose={handleCloseModal}></AddModal>}
      {showUpdateModal&&<AddModal up infoForm className="admin-modal" onClickClose={handleCloseModal}></AddModal>}
    </div>
  );
}
