import React, { useEffect, useState } from "react";
import { _getUserList, _delUser, _reg, _update } from "../../util/req";
import AddModal from '../../component/AddModal';
import Button from '../../component/button';
import './style.css';

export default function Index() {
  const [showAddModal,setAddModal] = useState(false);
  const handleClickAdd = () => {
    setAddModal(true)
  }
  const handleCloseModal = () => {
    setAddModal(false)
  }
  const [list, setList] = useState([]);
  const [infoForm, setInfoForm] = useState({
    username: "",
    password: "",
  });

  const gelist = async () => {
    var res = await _getUserList();
    setList(res.data);
  };
  const del = async (id) => {
    await _delUser(id);
    //alert("ok");
    gelist();
  };
  
  const save = async () => {
	if(infoForm.username.length>=4 && infoForm.password.length>=4 && infoForm.username.length<=20 && infoForm.password.length<=20) {

    if (infoForm._id) {
      await _update(infoForm);
    } else {
      await _reg(infoForm);
    }

    //alert("ok");
    gelist();
    setInfoForm({ username: "", password: "" });
 }else {alert("Username and password should between 4 and 20 characters");
} };

  const update = async (it) => {
    setInfoForm({ ...it });
  };

  useEffect(() => {
    gelist();
  }, []);

  return (
    <div className="page-container">
      <br />
      <br />
      <div className="button-container">
      <div className="user-button">
        <Button  onClick={handleClickAdd} englishLabel="Create User"/>
      </div>
      </div>
      
  


      <table className="table">
        <thead>
          <tr>
            <td>User Name</td>
            <td>Password</td>
            <td>Action</td>
          </tr>
        </thead>
        <tbody>
          {list.map((it, index) => (
            <tr key={index}>
              <td>{it.username}</td>
              <td>{it.password}</td>
              <td>
                <Button englishLabel="Update" onClick={() => update(it) }/>
                <Button englishLabel="Delete" onClick={() => del(it._id)}/>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {showAddModal&&<AddModal className="admin-modal" onClickClose={handleCloseModal}></AddModal>}
      
    </div>
  );
}
