import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import { _getPlacesLists } from "./../../util/req";
export default function Index() {
  const [List, setList] = useState([]);
  const [sort, setSort] = useState(false);

  const getList = async () => {
    var data = await _getPlacesLists();
    var mpdata = data.data.map((it, index) => ({
      ...it,
    }));
    mpdata.sort(
      (a, b) => a.wait.hospName.charCodeAt(0) - b.wait.hospName.charCodeAt(0)
    );
    setList(mpdata);
    console.log("data -> :", mpdata);
  };
  useEffect(() => {
    getList();
  }, []);
  useEffect(() => {
    const nwList = List;
    if (sort) {
      nwList.sort(
        (a, b) => a.wait.hospName.charCodeAt(0) - b.wait.hospName.charCodeAt(0)
      );
    } else {
      nwList.sort(
        (a, b) => b.wait.hospName.charCodeAt(0) - a.wait.hospName.charCodeAt(0)
      );
    }
    console.log("nwList -> :", nwList);

    setList(nwList);
  }, [List, sort]);

  return (
    <div className="info-center">
      <br />
      <br />
      <br />
      <table>
        <thead>
          <tr>
            <th style={{ width: "40%" }}>
              HOSPNAME
              {sort ? (
                <span
                  onClick={() => {
                    setSort(false);
                  }}
                >
                  ↑
                </span>
              ) : (
                <span
                  onClick={() => {
                    setSort(true);
                  }}
                >
                  ↓
                </span>
              )}
            </th>
            <th>NAME</th>
            <th>FORMATTED_ADDRESS</th>
            <th>WEBSITE</th>
            <th>TOPWAIT</th>
            <th>RATING</th>
            <th>ICON</th>
          </tr>
        </thead>
        <tbody>
          {List.map((it, index) => (
            <tr key={index}>
              <td>
                <Link to={`/index/info/${it.place_id}`}>
                  {it.wait.hospName}
                </Link>
              </td>
              <td>{it.name}</td>
              <td>{it.formatted_address}</td>
              <td>{it.website}</td>
              <td>{it.wait.topWait}</td>
              <td>{it.rating}</td>
              <td>
                <img src={it.icon} width="50px" height="50px" alt="" />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
