import React, { useEffect, useState } from "react";
import { _getUserList, _delUser, _reg, _update } from "../../util/req";
import Button from '../button';
import ModalWithTitle from '../ModalWithTitle';
import "./style.css";
import {
  _saveJson,
  _saveReviewsJson,
  _getPlacesLists,
  _delPlace,
  _updatePlace,
} from "../../util/req";

const PlaceModal = (props) => {
	const [list, setList] = useState([]);
	const [infoForm, setInfoForm] = useState({
    name: "",
    rating: "",
    formatted_phone_number: "",
    website: "",
    formatted_address: "",
  });
	const gelist = async () => {
    var res = await _getPlacesLists();
    setList(res.data);
  };
  const save = async () => {
    await _updatePlace(infoForm);

    gelist();
    setInfoForm({
      name: "",
      rating: "",
      formatted_phone_number: "",
      website: "",
      formatted_address: "",
    });
	};
	useEffect(() => {
    gelist();
  }, []);

  return(
    <ModalWithTitle englishTitle=" Add New Place" onClick={props.onClickClose} backgroundColor="#1F6332">
		<div className="addmodal-container">
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					name:{" "}
				</span>
			</div>
			<div className="addmodal-input-container">
				<input
						type="text"
						className="addmodal-input"
            value={infoForm.name}
            onChange={(e) => {
              setInfoForm({ ...infoForm, name: e.target.value });
            }}
          />
			</div>
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					rating:{" "}
				</span>
			</div>
      <div className="addmodal-input-container">
				<input
						type="text"
						className="addmodal-input"
            value={infoForm.rating}
            onChange={(e) => {
              setInfoForm({ ...infoForm, rating: e.target.value });
            }}
          />
			</div>
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					formatted_phone_number:{" "}
				</span>
			</div>
      <div className="addmodal-input-container">
				<input
						type="text"
						className="addmodal-input"
            value={infoForm.formatted_phone_number}
            onChange={(e) => {
              setInfoForm({
                ...infoForm,
                formatted_phone_number: e.target.value,
              });
            }}
        />
			</div>
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					website:{" "}
				</span>
			</div>
      <div className="addmodal-input-container">
				<input
						type="text"
						className="addmodal-input"
            value={infoForm.website}
            onChange={(e) => {
              setInfoForm({ ...infoForm, website: e.target.value });
            }}
        />
			</div>
			<div className="addmodal-title-container">
				<span className="addmodal-english-title">
					formatted_address:{" "}
				</span>
			</div>
      <div className="addmodal-input-container">
				<input
						type="text"
						className="addmodal-input"
            value={infoForm.formatted_address}
            onChange={(e) => {
              setInfoForm({ ...infoForm, formatted_address: e.target.value });
            }}
        />
			</div>
			<div className="addmodal-button-container">
				<div className="addmodal-button">
					<Button chineseLabel="確認" englishLabel="Confirm" onClick={save} >
						{infoForm._id ? "Save" : "Create"}
					</Button>
				</div>
      </div>
		</div>	
  	</ModalWithTitle>
  )

}

export default PlaceModal
