import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
  Link,
  NavLink,
} from "react-router-dom";

import Home from "./../Home";
import List from "./../List";
import Info from "./../Info";

function Layout() {
  const user = localStorage.getItem("user")
    ? JSON.parse(localStorage.getItem("user"))
    : {};

  const loginout = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <Router>
      <div className="hd">
        <nav>
          <NavLink to="/index/home" exact activeClassName="active">
            Home
          </NavLink>
          <NavLink to="/index/list" exact activeClassName="active">
            List
          </NavLink>
        </nav>
        <div>
          Welcome{""}
          {user.role === 1 ? "Administrator ," : ""}
          {user.username}, <span onClick={loginout}>退出</span>
        </div>
      </div>
      <Switch>
        <Route exact path="/index/home" component={Home} />
        <Route exact path="/index/list" component={List} />
        <Route exact path="/index/info/:place_id?" component={Info} />
        <Redirect to={"/index/home"} />
      </Switch>
    </Router>
  );
}

export default Layout;
