import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import Map from "../../component/Map";
import { _getPinfosList, _getPlaceInfo, _updateText } from "../../util/req";
import { API_KEY } from "./../Home";
import dayjs from "dayjs";

export default function Index() {
  const { place_id } = useParams();

  const [info, setInfo] = useState({
    wait: {},
    opening_hours: {
      open_now: false,
      weekday_text: [],
    },
    reviews: [],
  });
  const [placeJson, setPlaceJson] = useState([]);
  const [center, setCenter] = useState({
    lat: 0,
    lng: 0,
  });

  const [poss, setPoss] = useState("");

  useEffect(() => {
    const getInfo = async () => {
      var data = await _getPlaceInfo(place_id);
      setPlaceJson(data.data);
      setCenter({
        lat: Number(data.data[0].geometry.location.lat) || 0,
        lng: Number(data.data[0].geometry.location.lng) || 0,
      });
      setInfo({ ...data.data[0], reviews: data.data[0].review[0].reviews });
    };
    getInfo();
  }, [place_id]);

  const saveText = async () => {
    var oldData = [...info.reviews];
    var pushss = {
      name: JSON.parse(localStorage.getItem("user")).username,
      text: poss,
      rating: 4,
      relative_time_description: dayjs().format("YYYY-MM-DD HH:mm:ss"),
    };
    oldData.push(pushss);
    console.log("oldData -> :", oldData);

    await _updateText({
      data: JSON.stringify({
        place_id: info.place_id,
        reviews: oldData,
      }),
    });
    alert("ok");
    window.location.reload();
  };

  return (
    <div className="info-center">
      <p>Name:{info.name}</p>
      <p>Rating:{info.rating}</p>
      <p>user_Ratings_total:{info.user_ratings_total}</p>
      <p>website:{info.website}</p>
      <p>wait:{info.wait.topWait}</p>
      <p>open_now:{info.opening_hours && info.opening_hours.open_now}</p>
      <p>
        open weekday_text:{info.opening_hours && info.opening_hours.weekday_text}
      </p>
      <ul>
        {info.reviews.map((it, index) => (
          <li key={index}>
            <div>
              <Link to={it.author_url}>{it.author_name}</Link>
              {it.profile_photo_url && (
                <img
                  src={it.profile_photo_url}
                  width="35px"
                  height="35px"
                  alt=""
                />
              )}
            </div>
            <div>{it.text}</div>
            <div>{it.rating} stars</div>
            <div>{it.relative_time_description}</div>
          </li>
        ))}
      </ul>
      <hr />
      <br />
      <br />
      <div>
        <textarea
          name=""
          id=""
          cols="30"
          rows="10"
          style={{ width: "100%" }}
          value={poss}
          onChange={(e) => setPoss(e.target.value)}
        ></textarea>
        <button onClick={saveText}>Submit</button>
      </div>
      <br />
      <br />

      <Map
        loadingElement={<div style={{ height: `100%` }} />}
        containerElement={<div style={{ height: `600px` }} />}
        mapElement={<div style={{ height: `100%` }} />}
        initMapkers={placeJson}
        center={center}
      />
    </div>
  );
}
