import React, { useEffect, useState } from "react";
import Map from "../../component/Map";
import { StandaloneSearchBox } from "react-google-maps/lib/components/places/StandaloneSearchBox";

import { _getJson, _getPidsList, _getPinfosList } from "./../../util/req.js";

const API_KEY = "AIzaSyCvHcIZbtF80KUW23vCV_kmmr2EVixerAI";

export default function Index() {
  const refs = {};
  const [places, setPlaces] = useState([]);

  const [placeJson, setPlaceJson] = useState([]);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  const getJson = async () => {
    var res = await _getJson();
    console.log("res -> :", res);
    getPlaceList(res.data);
  };

  const getPlaceList = async (placeJson) => {
    var nameList = placeJson;

    var reqList = nameList.map(
      async (it, index) =>
        await _getPidsList(
          `/queryautocomplete/json?&key=${API_KEY}&input=${it.hospName}`
        )
    );

    var data = await Promise.all(reqList);
    var finalDataList = data
      .map((it) => it.predictions[0].place_id)
      .map(
        async (it, index) =>
          await _getPinfosList(`/details/json?&key=${API_KEY}&placeid=${it}`)
      );

    var finalData = Promise.all(finalDataList);

    console.log("finalData -> :", finalData);

    return [];
  };

  useEffect(() => {
    getJson();
  }, [getJson]);

  return (
    <div>
      <br />
      <div style={{ textAlign: "center", width: "420px", margin: "0 auto" }}>
        <StandaloneSearchBox
          ref={(res) => {
            refs.searchBox = res;
          }}
          onPlacesChanged={() => {
            const places = refs.searchBox.getPlaces();
            setPlaces([...places]);
          }}
        >
          <input
            type="text"
            placeholder="Customized your placeholder"
            style={{
              boxSizing: `border-box`,
              border: `1px solid transparent`,
              width: `100%`,
              height: `32px`,
              padding: `0 12px`,
              borderRadius: `3px`,
              boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
              fontSize: `14px`,
              outline: `none`,
              textOverflow: `ellipses`,
            }}
          />
        </StandaloneSearchBox>
        <ul>
          {places.map(
            ({ place_id, formatted_address, geometry: { location } }) => (
              <li key={place_id}>
                {formatted_address}
                {" at "}({location.lat()}, {location.lng()})
              </li>
            )
          )}
        </ul>
      </div>
      <br />
      <br />
      <br />
      <br />
      {/* <Map
        loadingElement={<div style={{ height: `100%` }} />}
        containerElement={<div style={{ height: `400px` }} />}
        mapElement={<div style={{ height: `100%` }} />}
      /> */}
    </div>
  );
}
