import React, { Component } from "react";
import { _login } from "./../../util/req.js";
import { withRouter } from "react-router-dom";

class Login extends Component {
  constructor() {
    super();
    this.state = {
      username: "a123",
      password: "123456",
    };
  }

  handleChange = (e, type) => {
    this.setState({
      [type]: e.target.value,
    });
  };

  loginHandle = async () => {
    const obj = {
      username: this.state.username,
      password: this.state.password,
    };
    console.log("obj -> :", obj);

    const res = await _login(obj);
    this.successHandle(res.data);
  };

  successHandle = (data) => {
    localStorage.setItem("user", JSON.stringify(data));
    console.log(data);
    this.props.history.push(data.role === "1" ? "/index/home" : "/index/user");
  };
  render() {
    return (
      <div>
        <div>
          <h3>Hospital Waiting Time Checking and Reviewing Platform</h3>
          <hr />
          <br />
        </div>
        username ：
        <input
          value={this.state.username}
          onChange={(e) => this.handleChange(e, "username")}
        />
        <br />
        password ：
        <input
          value={this.state.password}
          onChange={(e) => this.handleChange(e, "password")}
        />
        <br />
        <br />
        <button className="button button1" onClick={this.loginHandle}>
          Login
        </button>
      </div>
    );
  }
}

export default withRouter(Login);
