import React, { useEffect, useState } from "react";
import { _getUserList, _delUser } from "../../util/req";

export default function Index() {
  const [list, setList] = useState([]);

  const gelist = async () => {
    var res = await _getUserList();
    setList(res.data);
  };
  const del = async (id) => {
    await _delUser(id);
    gelist();
  };
  const update = async (id) => {};

  useEffect(() => {
    gelist();
  }, []);

  return (
    <div className="info-center">
      <br />
      <br />
      <br />
      <br />
      <table>
        <thead>
          <tr>
            <th>userName</th>
            <th>password</th>
            <th>action</th>
          </tr>
        </thead>
        <tbody>
          {list.map((it, index) => (
            <tr key={index}>
              <td>{it.username}</td>
              <td>{it.password}</td>
              <td>
                <button onClick={(it) => update(it)}>Update</button>
                <button onClick={() => del(it._id)}>Del</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
