import React, { useEffect, useState } from "react";
import {
  _saveJson,
  _saveReviewsJson,
  _getPlacesLists,
  _delPlace,
  _updatePlace,
} from "../../util/req";

export default function Index() {
  const [list, setList] = useState([]);
  const [infoForm, setInfoForm] = useState({
    name: "",
    rating: "",
    formatted_phone_number: "",
    website: "",
    formatted_address: "",
  });

  const gelist = async () => {
    var res = await _getPlacesLists();
    setList(res.data);
  };
  const del = async (id) => {
    await _delPlace(id);
    alert("ok");
    gelist();
  };
  const save = async () => {
    await _updatePlace(infoForm);
    alert("ok");
    gelist();
    setInfoForm({
      name: "",
      rating: "",
      formatted_phone_number: "",
      website: "",
      formatted_address: "",
    });
  };
  const update = async (it) => {
    setInfoForm({ ...it });
  };
  const created = async (it) => {
    await _saveJson();
    await _saveReviewsJson();
    alert("ok");
  };

  useEffect(() => {
    gelist();
  }, []);

  return (
    <div className="info-center">
      <br />
      <br />
      <p>
        <button type="button" onClick={created}>
          refesh data for API
        </button>
      </p>
      <br />
      <br />
      <form>
        <p>
          name:{" "}
          <input
            type="text"
            value={infoForm.name}
            onChange={(e) => {
              setInfoForm({ ...infoForm, name: e.target.value });
            }}
          />
        </p>
        <p>
          rating:{" "}
          <input
            type="text"
            value={infoForm.rating}
            onChange={(e) => {
              setInfoForm({ ...infoForm, rating: e.target.value });
            }}
          />
        </p>
        <p>
          formatted_phone_number:{" "}
          <input
            type="text"
            value={infoForm.formatted_phone_number}
            onChange={(e) => {
              setInfoForm({
                ...infoForm,
                formatted_phone_number: e.target.value,
              });
            }}
          />
        </p>
        <p>
          website:{" "}
          <input
            type="text"
            value={infoForm.website}
            onChange={(e) => {
              setInfoForm({ ...infoForm, website: e.target.value });
            }}
          />
        </p>
        <p>
          formatted_address:{" "}
          <input
            type="text"
            value={infoForm.formatted_address}
            onChange={(e) => {
              setInfoForm({ ...infoForm, formatted_address: e.target.value });
            }}
          />
        </p>
        <p>
          <button type="button" onClick={save}>
            Save
          </button>
        </p>
      </form>

      <br />
      <br />
      <div style={{ overflowX: "auto" }}>
        <table>
          <thead>
            <tr>
              <th>name</th>
              <th>rating</th>
              <th>user_ratings_total</th>
              <th>formatted_phone_number</th>
              <th>website</th>
              <th>formatted_address</th>
              <th>action</th>
            </tr>
          </thead>
          <tbody>
            {list.map((it, index) => (
              <tr key={index}>
                <td>{it.name}</td>
                <td>{it.rating}</td>
                <td>{it.user_ratings_total}</td>
                <td>{it.formatted_phone_number}</td>
                <td>{it.website}</td>
                <td>{it.formatted_address}</td>
                <td>
                  <button onClick={() => update(it)}>Update</button>
                  <button onClick={() => del(it._id)}>Del</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
