import React, { useEffect, useState } from "react";
import { _getUserList, _delUser, _reg, _update } from "../../util/req";

export default function Index() {
  const [list, setList] = useState([]);
  const [infoForm, setInfoForm] = useState({
    username: "",
    password: "",
  });

  const gelist = async () => {
    var res = await _getUserList();
    setList(res.data);
  };
  const del = async (id) => {
    await _delUser(id);
    alert("ok");
    gelist();
  };
  const save = async () => {
    if (infoForm._id) {
      await _update(infoForm);
    } else {
      await _reg(infoForm);
    }
    alert("ok");
    gelist();
    setInfoForm({ username: "", password: "" });
  };
  const update = async (it) => {
    setInfoForm({ ...it });
  };

  useEffect(() => {
    gelist();
  }, []);

  return (
    <div className="info-center">
      <br />
      <br />

      <form>
        <p>
          username:{" "}
          <input
            type="text"
            value={infoForm.username}
            onChange={(e) => {
              setInfoForm({ ...infoForm, username: e.target.value });
            }}
          />
        </p>
        <p>
          password:{" "}
          <input
            type="text"
            value={infoForm.password}
            onChange={(e) => {
              setInfoForm({ ...infoForm, password: e.target.value });
            }}
          />
        </p>
        <p>
          <button type="button" onClick={save}>
            {infoForm._id ? "Save" : "Create"}
          </button>
        </p>
      </form>

      <br />
      <br />
      <table>
        <thead>
          <tr>
            <th>userName</th>
            <th>password</th>
            <th>action</th>
          </tr>
        </thead>
        <tbody>
          {list.map((it, index) => (
            <tr key={index}>
              <td>{it.username}</td>
              <td>{it.password}</td>
              <td>
                <button onClick={() => update(it)}>Update</button>
                <button onClick={() => del(it._id)}>Del</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
